  console.log('Content', props)
  return (
    <div>
      {/*{props.part} {props.exercises}*/}
      <Part part={props[0].name} exercises={props[0].exercises}/>

    </div>
  )
}

const Total = (props) => {
  console.log('Total', props)
  return (
    <div>
      Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}
    </div>
  )
}

const Part = (props) => {
